section.script:

      $( document ).ready(function() {
        $.ajax({
          type: "GET",
          url: "../../api/v1/search?q=objectType:expression&rows=0&facet.field=incip.main&facet.query=objectType:expression&facet.sort=index&facet=on&facet.limit=-1&wt=json&json.wrf=?",
          dataType: "jsonp",
          success: function (data) {
            var incipit = "";
            $(data.facet_counts.facet_fields["incip.main"]).each(function(i, int){
              if (i % 2 === 0) {
                incipit = int;
              }
              else {
                $('#cmo_incipitList').append($('
')
                  .append('[ ' + int + ' ] ' + incipit + '')); 
              }
            });
          },
        });
      });
