section.script:
    $( document ).ready(function() {
      $.ajax({
        type: "GET",
        url: "../../api/v1/search?q=incip:*&rows=2000&fl=id,%20incip.main,%20incip&sort=incip.main%20asc&wt=json&json.wrf=?",
        dataType: "jsonp",
        success: function (data) {
          $(data.response.docs).each(function(i, doc){
          var incipitText = (doc['incip.main']  !== undefined) ? doc['incip.main'] : doc['incip'][0];
            $('#cmo_incipitList').append($('
')
              .append('' + incipitText + ''));
          });
        },
      });
    });
