section.script:

      $( document ).ready(function() {
        $.ajax({
          type: "GET",
          url: "../../api/v1/search?q=objectType:expression&rows=0&facet.field=title.type.alt&facet.query=objectType:expression&facet.sort=index&facet.limit=-1&facet=on&wt=json&json.wrf=?",
          dataType: "jsonp",
          success: function (data) {
            var poetTitle = "";
            $(data.facet_counts.facet_fields["title.type.alt"]).each(function(i, int){
              if (i % 2 === 0) {
                poetTitle = int;
              }
              else {
                $('#cmo_titleList').append($('
')
                .append('[ ' + int + ' ] ' + poetTitle + '')); 
              }
            });
          },
        });
      });
